changeSet.sql:

CREATE OR REPLACE PACKAGE utils_pkg
        AS
        FUNCTION generate_uuid
        RETURN VARCHAR2;
END utils_pkg;


CREATE OR REPLACE PACKAGE BODY utils_pkg
    AS
      FUNCTION generate_uuid
      RETURN VARCHAR2
      IS
        v_uuid VARCHAR2(36);
BEGIN
SELECT LOWER(REGEXP_REPLACE(RAWTOHEX(SYS_GUID()), '([A-F0-9]{8})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{12})', '\1-\2-\3-\4-\5'))
INTO v_uuid
FROM DUAL;

RETURN v_uuid;
END generate_uuid;
END utils_pkg;
/
